import React, { Component } from 'react'
import { Container, Row, Col } from 'reactstrap';
import Tilt from 'react-parallax-tilt';
import Parallax from 'react-springy-parallax'
import { Spring } from 'react-spring'
import { Button } from 'react-bootstrap';
import ui from '../assets/hero-ui.png'
import cloud from '../assets/1.png'
import steps from '../assets/steps.png'
import {NavLink,Collapse,Navbar,NavbarToggler,NavbarBrand,Nav,NavItem} from 'reactstrap';
import '../Navbar/NavBar.css'
import '../Home/Home.css'

class Home extends Component {  
  constructor(props) {
    super(props);

    this.toggleNavbar = this.toggleNavbar.bind(this);
    this.closeNavbar = this.closeNavbar.bind(this);
    this.state = {
    collapsed: true
    };
}

toggleNavbar() {
    this.setState({
    collapsed: !this.state.collapsed
    });
}

closeNavbar() {
    if (this.state.collapsed !== true) {
    this.toggleNavbar();
    }
}
  render (){
    const styles = {
      fontFamily: 'Menlo-Regular, Menlo, monospace',
      fontSize: 14,
      lineHeight: '10px',
      color: 'white',
      display: 'flex', alignItems: 'center', justifyContent: 'center'
  }
    return (
        <div >

<Parallax ref="parallax" pages={3}>
         <Parallax.Layer offset={0} speed={1} style={{ backgroundColor: '#243B4A' }} />
                <Parallax.Layer offset={1} speed={1} style={{ backgroundColor: '#805E73' }} />
                <Parallax.Layer offset={2} speed={1} style={{ backgroundColor: '#87BCDE' }} />


            
                <Parallax.Layer
                    offset={0}
                    speed={0.5}
                    style={styles}
                  >
                    
                    <div className="header-content">
                    <div className="Nav">
        <Navbar className="navbar navbar-expand-md  navbar-dark ">
       <NavbarBrand tag={NavLink} to="/">givable </NavbarBrand>
       <NavbarToggler onClick={this.toggleNavbar} />
       <Collapse isOpen={!this.state.collapsed} navbar>
         <Nav className="mr-auto" navbar>
         <NavItem>
              <NavLink href="/components/">Components</NavLink>
            </NavItem>
            <NavItem>
              <NavLink href="https://github.com/reactstrap/reactstrap">GitHub</NavLink>
            </NavItem>
          
         </Nav>
       </Collapse>
     </Navbar>
   
     </div>
            
            <Container>
                    <Row>
                        <Col className="header-text" md="6">
                        <h1 className="h1-text">An effortless donation experience for ambitious charities</h1>
                        <br/>
                        <h5>Coming Soon...</h5>
                        <a  target="_blank" className="andriod m-button"></a>
                        <a  target="_blank" className="iphone m-button"></a>
    
                        </Col>
    
                        <Col md="6">
                        <Tilt>
    
                        <img className="givable-img" src={ui}  alt="Givable" />
                        </Tilt>
    
                        </Col>
                      
                    </Row>
                   
                 </Container>
                 </div>

                </Parallax.Layer>


                <Parallax.Layer
                    offset={1}
                    speed={-0.1}
                    style={styles}
                   >
                      <section>
             <div className="contant1">
                 <div className="contant1-text">
                 <h2 style={{'color':'#E2527A'}}>
                 Increase online donation revenue & better understand your donors
                 </h2>
                 <p>Givable is a proven mobile-first 4 step donation funnel branded to your charity, including a beautifully simple donation dashboard with detailed reporting</p>
             </div >
             
             </div>
             </section>
                </Parallax.Layer>

                <Parallax.Layer
                    offset={2}
                    speed={0.5}
                    style={styles}
                    >
                      <Container>
                <Row>
                    <Col md="6">
                    <img src={steps} style={{'width': '100%',    'max-height': 'auto'}}/>
                    </Col>

                    <Col md="6">
                        <h1 style={{'color':'#E2527A'}}>Proven 4 step funnel</h1>
                        <p>A clear and simple experience consisting of the donation amount, donor details with address look-up, Gift Aid opt-in and payment details with multiple payment methods</p>
                    </Col>
                </Row>
             </Container>

                </Parallax.Layer>

             
             </Parallax>
      </div>
      
   
    )
  }
   
}

export default Home;
